sysauto_script.script:
//Creating variables for Joe employee
var reqFor = '681ccaf9c0a8016400b98a06818d57c7';
var reqForEmail = 'employee@example.com';

var rightNow = new GlideDateTime();

var gr = new GlideRecord('x_58872_needit_needit');
gr.addQuery('u_requested_for', '=', '6816f79cc0a8016401c5a33be04be441');
gr.addQuery('u_when_needed', '>', rightNow.getDate());
gr.query();

while(gr.next()) {
		gr.setValue('u_requested_for', reqFor);
		gs.info('Record changed req for');
		gr.setValue('u_requested_for_email', reqForEmail.toString());

		gr.update();
		//Log the change
// 		gs.info("Record changed 2: " + gr.number);

}
